import React, { useState } from "react"
import Togglable from "./Togglable"

const toggle = () => <button>view</button>

const Blog = ({ blog }) => {
  const [detailedView, setDetailedView] = useState(true)

  return (
    <div>
      {detailedView === true ? (
        <div>
          {blog.title} {blog.author}
          <button onClick={() => setDetailedView(false)}>view</button>
        </div>
      ) : (
        <div>
          {blog.title}
          {blog.author}
          {blog.url}
          {blog.likes}
          <button onClick={() => setDetailedView(true)}>hide</button>
        </div>
      )}
    </div>
  )

  /*   return (
    <div>
      {blog.title} {blog.author}
    </div>
  ) */
}

export default Blog
