import React, { useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { setNotification } from "../reducers/notificationReducer"
import { addLike, remove } from "../reducers/blogReducer"
import { useParams } from "react-router-dom"

const Blog = () => {
  const [detailedView, setDetailedView] = useState(true)
  const dispatch = useDispatch()
  const id = useParams().id
  const user = useSelector((state) => state.user)
  const blogs = useSelector((state) => state.blogs)
  const blog = blogs.find((b) => b.id === id)

  const like = (blog) => {
    dispatch(addLike(blog))
    dispatch(setNotification(`you liked ${blog.title}`, 10))
  }

  const removeBlog = (blog) => {
    dispatch(remove(blog))
    dispatch(setNotification(`you removed ${blog.title}`, 10))
  }

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: "solid",
    borderWidth: 1,
    marginBottom: 5
  }

  if (!blog) return null

  return (
    <div style={blogStyle} className="blog">
      Title: {blog.title} <br />
      Author: {blog.author}
      <br />
      URL: {blog.url}
      <br />
      Likes: {blog.likes} <button onClick={() => like(blog)}>likes</button>
      <br />
      {blog.user.username === user.username ? (
        <button onClick={() => removeBlog(blog)}>delete</button>
      ) : (
        <div></div>
      )}
    </div>
  )

  return (
    <div>
      {detailedView === true ? (
        <div style={blogStyle} className="blog">
          {blog.title}
          <button text="view" onClick={() => setDetailedView(false)}>
            view
          </button>
        </div>
      ) : (
        <div style={blogStyle} className="blog">
          Title: {blog.title}{" "}
          <button onClick={() => setDetailedView(true)}>hide</button>
          <br />
          Author: {blog.author}
          <br />
          URL: {blog.url}
          <br />
          Likes: {blog.likes} <button onClick={() => like(blog)}>likes</button>
          <br />
          {blog.user.username === user.username ? (
            <button onClick={() => removeBlog(blog)}>delete</button>
          ) : (
            <div></div>
          )}
        </div>
      )}
    </div>
  )
}

export default Blog
