import React, { useState, useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { setNotification } from "../reducers/notificationReducer"
import { addLike, remove } from "../reducers/blogReducer"
import { useParams } from "react-router-dom"
import { initializeComments } from "../reducers/commentReducer"

const Blog = () => {
  const [detailedView, setDetailedView] = useState(true)
  const dispatch = useDispatch()
  const id = useParams().id
  const user = useSelector((state) => state.user)
  const blogs = useSelector((state) => state.blogs)
  const blog = blogs.find((b) => b.id === id)
  const comments = useSelector((state) => state.comments)

  useEffect(() => {
    dispatch(initializeComments(id))
  }, [id])

  const like = (blog) => {
    dispatch(addLike(blog))
    dispatch(setNotification(`you liked ${blog.title}`, 5))
  }

  const removeBlog = (blog) => {
    dispatch(remove(blog))
    dispatch(setNotification(`you removed ${blog.title}`, 5))
  }

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: "solid",
    borderWidth: 1,
    marginBottom: 5
  }

  const commentView = () => {
    if (!comments) return null
    return (
      <div>
        {comments.map((comment) => (
          <li key={comment.id}>{comment.content}</li>
        ))}
      </div>
    )
  }

  const commentForm = () => {
    console.log()
    const addComment = (event) => {
      event.preventDefault()
      const newComment = {
        content: event.target.value
      }
    }

    return (
      <div>
        <h2>Add comment</h2>
        <form onSubmit={addComment}>
          <input name="content" />
          <button type="submit">create</button>
        </form>
      </div>
    )
  }

  if (!blog) return null
  return (
    <div style={blogStyle} className="blog">
      <h2>{blog.title}</h2>
      <br />
      Author: {blog.author}
      <br />
      URL: <a href={blog.url}>{blog.url}</a>
      <br />
      Likes: {blog.likes} <button onClick={() => like(blog)}>likes</button>
      <br />
      {blog.user.username === user.username ? (
        <button onClick={() => removeBlog(blog)}>delete</button>
      ) : (
        <div></div>
      )}
      <h3>comments</h3>
      {commentForm()}
      {commentView()}
    </div>
  )
}

export default Blog
