import React, { useState, useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { setNotification } from "../reducers/notificationReducer"
import { addLike, remove } from "../reducers/blogReducer"
import { useParams } from "react-router-dom"
import { initializeComments } from "../reducers/commentReducer"
import { newComment } from "../reducers/commentReducer"

const Blog = () => {
  const [detailedView, setDetailedView] = useState(true)
  const dispatch = useDispatch()
  const id = useParams().id
  const user = useSelector((state) => state.user)
  const blogs = useSelector((state) => state.blogs)
  const blog = blogs.find((b) => b.id === id)
  const comments = useSelector((state) => state.comments)

  useEffect(() => {
    dispatch(initializeComments(id))
  }, [id])

  const like = (blog) => {
    dispatch(addLike(blog))
    dispatch(setNotification(`you liked ${blog.title}`, 5))
  }

  const removeBlog = (blog) => {
    if (window.confirm(`Remove ${blog.title}?`)) {
      dispatch(remove(blog))
      dispatch(setNotification(`you removed ${blog.title}`, 5))
    }
  }

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: "solid",
    borderWidth: 1,
    marginBottom: 5
  }

  const commentView = () => {
    console.log(comments)
    if (comments.length == 0) return null
    return (
      <div class="card">
        <h6 class="card-header">comments</h6>
        {comments.map((comment) => (
          <li class="list-group-item" key={comment.id}>
            {comment.content}
          </li>
        ))}
      </div>
    )
  }

  const commentForm = () => {
    const addComment = (event) => {
      event.preventDefault()
      const comment = {
        content: event.target.content.value
      }
      event.target.content.value = ""
      dispatch(newComment(id, comment))
    }

    return (
      <div>
        <h4>Add comment</h4>
        <form onSubmit={addComment}>
          <input name="content" />
          <button type="submit">create</button>
        </form>
      </div>
    )
  }

  if (!blog) return null
  return (
    <div>
      <div class="card">
        <div class="card-body">
          <h2 class="card-title">{blog.title}</h2>{" "}
          <button class="btn btn-success" onClick={() => like(blog)}>
            like
          </button>
          <div class="card-text">
            <ul class="list-group list-group-flush">
              <li class="list-group-item">Author: {blog.author}</li>
              <li class="list-group-item">
                URL:
                <a href={blog.url} class="card-link">
                  {` ${blog.url}`}
                </a>
              </li>
              <li class="list-group-item">Likes: {blog.likes}</li>
            </ul>
            {blog.user.username === user.username ? (
              <button class="btn btn-danger" onClick={() => removeBlog(blog)}>
                delete
              </button>
            ) : (
              <div></div>
            )}
          </div>
        </div>
        <div>
          {commentView()}
          {commentForm()}
        </div>
      </div>
    </div>
  )
}

export default Blog
