import React from "react"
import { useSelector } from "react-redux"

const Notification = () => {
  const not = useSelector(({ notification }) => {
    return <div style={style}>{not}</div>
  })
  const style = {
    border: "solid",
    padding: 10,
    borderWidth: 1
  }
}

export default Notification
