import React, { useState, useEffect } from "react"
import Blog from "./components/Blog"
import blogService from "./services/blogs"
import loginService from "./services/login"
import BlogForm from "./components/BlogForm"
import Togglable from "./components/Togglable"

const App = () => {
  const [errorMessage, setErrorMessage] = useState(null)
  const [message, setMessage] = useState(null)
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const [user, setUser] = useState(null)
  const [newBlog, setNewBlog] = useState({
    blogTitle: "",
    blogAuthor: "",
    blogUrl: "",
  })

  useEffect(() => {
    blogService.getAll().then((blogs) => setBlogs(blogs))
  }, [])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem("loggedBlogappUser")
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  const Notification = ({ message }) => {
    if (message === null) {
      return null
    }

    return <div className="error">{message}</div>
  }

  const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({
        username,
        password,
      })

      window.localStorage.setItem("loggedBlogappUser", JSON.stringify(user))

      blogService.setToken(user.token)
      setUser(user)
      setUsername("")
      setPassword("")
    } catch (exception) {
      setErrorMessage("wrong credentials")
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
  }

  const handleBlogChange = (event) => {
    setNewBlog(event.target.value)
  }

  const addBlog = (event) => {
    event.preventDefault()
    const blogObject = {
      title: newBlog.blogTitle,
      author: newBlog.blogAuthor,
      url: newBlog.blogUrl,
    }
    console.log("title", newBlog.blogTitle, blogObject.title)

    blogService.create(blogObject).then((returnedBlog) => {
      setBlogs(blogs.concat(returnedBlog))
      setNewBlog("")
      setMessage(`a new blog ${blogObject.title} by ${blogObject.author} added`)
      setTimeout(() => {
        setMessage(null)
      }, 5000)
    })
  }

  const logout = () => {
    window.localStorage.clear()
    setUser(null)
    setPassword("")
  }

  const blogForm = () => (
    <BlogForm
      onSubmit={addBlog}
      title={title}
      handleTitleChange={({ target }) =>
        setNewBlog((newBlog.blogTitle = target.value))
      }
      handleAuthorChange={({ target }) =>
        setNewBlog((newBlog.blogAuthor = target.value))
      }
      handleUrlChange={({ target }) =>
        setNewBlog((newBlog.blogUrl = target.value))
      }
    />
  )

  return (
    <div>
      <h1>Blogs</h1>

      <Notification message={errorMessage === null ? message : errorMessage} />

      <h2>Login</h2>
      {user === null ? (
        <LoginForm />
      ) : (
        <div>
          <p>
            {user.name} logged in{" "}
            <button onClick={() => logout()}>logout</button>
          </p>

          {blogForm()}

          <h2>All blogs</h2>
          {blogs.map((blog) => (
            <Blog key={blog.id} blog={blog} />
          ))}
        </div>
      )}
    </div>
  )
}

const LoginForm = ({
  handleLogin,
  username,
  setUsername,
  password,
  setPassword,
}) => (
  <form onSubmit={handleLogin}>
    <div>
      username
      <input
        type="text"
        value={username}
        name="Username"
        onChange={({ target }) => setUsername(target.value)}
      />
    </div>
    <div>
      password
      <input
        type="password"
        value={password}
        name="Password"
        onChange={({ target }) => setPassword(target.value)}
      />
    </div>
    <button type="submit">login</button>
  </form>
)

export default App
