import React, { useState } from "react"
import blogService from "../services/blogs"

const Blog = ({ blog }) => {
  const [detailedView, setDetailedView] = useState(true)

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: "solid",
    borderWidth: 1,
    marginBottom: 5,
  }

  const addLike = () => {
    const newBlog = {
      user: blog.user,
      title: blog.title,
      author: blog.author,
      url: blog.id,
      likes: blog.likes + 1,
    }

    blogService.update(blog.id, newBlog)
  }

  const remove = async () => {
    if (window.confirm(`Delete ${blog.title}?`)) {
      await blogService.remove(blog.id)
      setDetailedView(false)
    }
  }

  return (
    <div>
      {detailedView === true ? (
        <div style={blogStyle}>
          {blog.title}
          <button onClick={() => setDetailedView(false)}>view</button>
        </div>
      ) : (
        <div style={blogStyle}>
          Title: {blog.title}{" "}
          <button onClick={() => setDetailedView(true)}>hide</button>
          <br />
          Author: {blog.author}
          <br />
          URL: {blog.url}
          <br />
          Likes: {blog.likes} <button onClick={() => addLike()}>likes</button>
          <br />
          {blog.user.username ===
          JSON.parse(window.localStorage.getItem("loggedBlogappUser"))
            .username ? (
            <button onClick={() => remove()}>delete</button>
          ) : (
            <div></div>
          )}
        </div>
      )}
    </div>
  )
}

export default Blog
