import commentService from "../services/comment"

const reducer = (state = [], action) => {
  switch (action.type) {
    case "INIT_COMMENTS":
      return action.data
    case "NEW_COMMENT":
      return [...state, action.content]
    case "DELETE_BLOG":
      return action.data
    default:
      return state
  }
}

export const remove = (blog) => {
  return async (dispatch) => {
    const remainingBlogs = await commentService.remove(blog.id)
    dispatch({
      type: "DELETE_BLOG",
      data: remainingBlogs
    })
  }
}

export const newComment = (id, content) => {
  return async (dispatch) => {
    const comment = await commentService.create(id, content)
    dispatch({
      type: "NEW_COMMENT",
      content: comment
    })
  }
}

export const initializeComments = (id) => {
  return async (dispatch) => {
    const comments = await commentService.getAll(id)
    console.log("comments in reducer", comments)
    dispatch({
      type: "INIT_COMMENTS",
      data: comments
    })
  }
}

export default reducer
