import React, { useState } from "react"

const Blog = ({ blog }) => {
  const [detailedView, setDetailedView] = useState(true)

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: "solid",
    borderWidth: 1,
    marginBottom: 5,
  }

  return (
    <div>
      {detailedView === true ? (
        <div style={blogStyle}>
          {blog.title}
          <button onClick={() => setDetailedView(false)}>view</button>
        </div>
      ) : (
        <div style={blogStyle}>
          Title: {blog.title}{" "}
          <button onClick={() => setDetailedView(true)}>hide</button>
          <br />
          Author: {blog.author}
          <br />
          URL: {blog.url}
          <br />
          Likes: {blog.likes}{" "}
          <button onClick={() => setDetailedView(false)}>likes</button>
        </div>
      )}
    </div>
  )

  /*   return (
    <div>
      {blog.title} {blog.author}
    </div>
  ) */
}

export default Blog
