import commentService from "../services/comment"

const reducer = (state = [], action) => {
  switch (action.type) {
    case "INIT_COMMENTS":
      return action.data
    case "NEW_BLOG":
      return [...state, action.content]
    case "LIKE_BLOG":
      const id = action.data.updatedBlog.id
      const blogToChange = state.find((a) => a.id === id)
      const changedBlog = {
        ...blogToChange,
        likes: blogToChange.likes + 1
      }
      return state.map((blog) => (blog.id !== id ? blog : changedBlog))
    case "DELETE_BLOG":
      return action.data
    case "SET_TOKEN":
      return state
    default:
      return state
  }
}

export const remove = (blog) => {
  return async (dispatch) => {
    const remainingBlogs = await commentService.remove(blog.id)
    dispatch({
      type: "DELETE_BLOG",
      data: remainingBlogs
    })
  }
}

export const newBlog = (content) => {
  return async (dispatch) => {
    const newBlog = await commentService.create(content)
    dispatch({
      type: "NEW_BLOG",
      content: newBlog
    })
  }
}

export const initializeComments = () => {
  return async (dispatch) => {
    const comments = await commentService.getAll()
    console.log("comments in reducer", comments)
    dispatch({
      type: "INIT_COMMENTS",
      data: comments
    })
  }
}

export default reducer
