import React, { useState } from "react"

const Blog = ({ blog }) => {
  const [detailedView, setDetailedView] = useState(true)

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: "solid",
    borderWidth: 1,
    marginBottom: 5,
  }

  return (
    <div>
      {detailedView === true ? (
        <div style={blogStyle}>
          {blog.title} {blog.author}
          <button onClick={() => setDetailedView(false)}>view</button>
        </div>
      ) : (
        <div>
          {blog.title}
          {blog.author}
          {blog.url}
          {blog.likes}
          <button onClick={() => setDetailedView(true)}>hide</button>
        </div>
      )}
    </div>
  )

  /*   return (
    <div>
      {blog.title} {blog.author}
    </div>
  ) */
}

export default Blog
